* gen_salt
*
* Returns PWD_SALT_LENGTH random bytes suitable for a salt
* 48 by default if no setting exists
*
* @returns VARCHAR2
*
******************************************************************************/
FUNCTION gen_salt
  RETURN VARCHAR2
AS
BEGIN
  RETURN UTL_RAW.CAST_TO_VARCHAR2( UTL_ENCODE.BASE64_ENCODE( SYS.dbms_crypto.randombytes(
  to_number( tasker.app_settings.get( 'PWD_SALT_LENGTH', 48 ) ) ) ) );
END gen_salt;
/**
* gen_hash
*
* Hashes a password and salt using PBKDF2
*
* @param p_password:varchar2  the password to hash
* @param p_salt:varchar2      the salt to use (use gen_salt)
* @return varchar2            the hash
*
******************************************************************************/
FUNCTION gen_hash(
    p_password   VARCHAR2,
    p_salt       VARCHAR2,
    p_iterations INTEGER DEFAULT NULL,
    p_length     INTEGER DEFAULT NULL )
  RETURN VARCHAR2
AS
  iterations INTEGER := p_iterations;
  LEN        INTEGER := p_length;
BEGIN
  IF iterations IS NULL THEN
    iterations := to_number( tasker.app_settings.get( 'DEFAULT_PWD_HASH_ITER', 4096 ) );
  END IF;
  IF LEN IS NULL THEN
    LEN := to_number( tasker.app_settings.get( 'PWD_HASH_LENGTH', 128 ) );
  END IF;
  RETURN pbkdf2( p_password, p_salt, iterations, LEN );
END gen_hash;
FUNCTION make_token_part
  RETURN VARCHAR2
AS
BEGIN
  RETURN HEXTORAW( SYS.dbms_crypto.randombytes( to_number( tasker.app_settings.get(
  'TOKEN_PART_LENGTH', 128 ) ) ) );
END;
PROCEDURE make_unique_token(
    p_salt VARCHAR2 DEFAULT NULL,
    p_client_token OUT VARCHAR2,
    p_db_token OUT VARCHAR2,
    p_partial_token OUT VARCHAR2 )
AS
  token_1      VARCHAR2( 1998 );
  token_2      VARCHAR2( 1998 );
  token_1_hash VARCHAR2( 1998 );
  dups         NUMBER := 0;
BEGIN
  /*
  * Notes: tokens are of the form: salt.token
  * Tokens in the database are stored using salt.token-hash
  * the idea here being that we don't want to continuously send the username
  * over the wire; this creates an easy attack surface.
  */
  LOOP
    token_1 := make_token_part( );
    token_2 := make_token_part( );
    -- client gets a token just of these two parts
    -- hash with session salt once (client will need to do this as well)
    IF p_salt IS NOT NULL THEN
      token_1_hash := gen_hash( token_1, p_salt, to_number( tasker.app_settings.get(
      'TOKEN_HASH_ITER', 256 ) ), to_number( tasker.app_settings.get( 'TOKEN_PART_LENGTH'
      , 128 ) ) );
    END IF;
    p_partial_token := token_2 || token_1_hash; -- testing ONLY
    -- internally hash with the same session salt again
    IF p_salt IS NOT NULL THEN
      token_1_hash := gen_hash( token_1_hash, p_salt, to_number( tasker.app_settings.get(
      'TOKEN_HASH_ITER', 256 ) ), to_number( tasker.app_settings.get( 'TOKEN_PART_LENGTH'
      , 128 ) ) );
    END IF;
    -- hash with the "pepper", or token 2
    token_1_hash := gen_hash( token_1_hash, token_2, to_number( tasker.app_settings.get(
    'TOKEN_HASH_ITER', 256 ) ), to_number( tasker.app_settings.get( 'TOKEN_PART_LENGTH',
    128 ) ) );
    p_client_token := token_2 || token_1;
    p_db_token := token_2 || token_1_hash;
    -- make sure no one else has this token (unlikely)
    BEGIN
       SELECT COUNT( * ) INTO dups FROM sessions WHERE token = p_db_token;
    EXCEPTION
    WHEN NO_DATA_FOUND THEN
      dups := 0;
    WHEN OTHERS THEN
      p_partial_token := NULL;
      p_client_token := NULL;
      p_db_token := NULL;
      raise;
    END;
    -- if we don't have a dup, exit
    EXIT
  WHEN DUPS = 0;
    -- if we do, go around again
  END LOOP;
END;
FUNCTION is_user_connected(
    p_user_id VARCHAR2 )
  RETURN BOOLEAN
AS
  current_session sessions%rowtype;
BEGIN
  BEGIN
     SELECT *
       INTO current_session
       FROM tasker.sessions
      WHERE user_id = p_user_id
      AND sysdate < expiry;
  EXCEPTION
  WHEN NO_DATA_FOUND THEN
    RETURN false;
  WHEN OTHERS THEN
    raise;
  END;
  RETURN true;
END;
FUNCTION authenticate_user(
    p_user_id   VARCHAR2,
    p_candidate VARCHAR2,
    p_session_id OUT NUMBER,
    p_next_token OUT VARCHAR2,
    p_session_salt OUT VARCHAR2 )
  RETURN VARCHAR2
AS
  pragma autonomous_transaction;
  client_token VARCHAR2( 4000 );
  db_token     VARCHAR2( 4000 );
  dev_token    VARCHAR2( 4000 );
  dups         NUMBER := 0;
  session_id   INTEGER := 0;
  session_salt VARCHAR2( 4000 );
BEGIN
  IF verify_password( p_user_id, p_candidate ) = 'Y' THEN
    session_salt := make_token_part( );
    p_session_salt := session_salt;
    make_unique_token( session_salt, client_token, db_token, dev_token );
    session_id := sessions_seq.nextval( );
     INSERT
       INTO sessions
      (
        id, user_id, token, expiry, salt,
        client_token -- don't store client-token in PROD
      )
      VALUES
      (
        session_id, p_user_id, db_token, sysdate + to_number( tasker.app_settings.get(
        'TOKEN_EXPIRY' ) ), session_salt, dev_token -- don't store client-token in PROD
      );
    COMMIT;
  ELSE
    tasker.utils.E_ACCESS_DENIED( );
    RETURN 'N'; -- dead, but for clarity
  END IF;
  p_session_id := session_id;
  p_next_token := client_token;
  RETURN 'Y';
END;
PROCEDURE end_session
  (
    p_session_id NUMBER
  )
AS
  pragma autonomous_transaction;
BEGIN
   DELETE FROM tasker.sessions WHERE id = p_session_id;
  COMMIT;
END;
FUNCTION verify_token(
    p_session_id NUMBER,
    p_token      VARCHAR2,
    p_auth_user OUT VARCHAR2,
    p_next_token OUT VARCHAR2 )
  RETURN VARCHAR2
AS
  pragma autonomous_transaction;
  current_session sessions%ROWTYPE;
  candidate_salt       VARCHAR2( 4000 );
  candidate_token      VARCHAR2( 4000 );
  candidate_token_hash VARCHAR2( 4000 );
  token_part_length    NUMBER := 0;
  db_token             VARCHAR2( 4000 );
  client_token         VARCHAR2( 4000 );
  dev_token            VARCHAR2( 4000 );
BEGIN
  -- token are of the form salt||token
  -- token part lengths are determined by TOKEN_PART_LENGTH
  token_part_length := to_number( tasker.app_settings.get( 'TOKEN_PART_LENGTH', 128 ) ) *
  2;
  IF LENGTH( p_token ) >=( token_part_length * 2 ) THEN
    candidate_salt := SUBSTR( p_token, 1, token_part_length );
    candidate_token := SUBSTR( p_token, LENGTH( p_token ) - token_part_length + 1, LENGTH
    ( p_token ) );
    -- the incoming token is expected to be hashed with the session salt once already
    -- get the corresponding session, if it exists
    BEGIN
       SELECT *
         INTO current_session
         FROM sessions
        WHERE id = p_session_id
        AND sysdate < expiry;
    EXCEPTION
    WHEN NO_DATA_FOUND THEN
      RETURN 'N';
    WHEN OTHERS THEN
      raise;
    END;
    -- with the session info, hash the candidate again
    candidate_token_hash := gen_hash( candidate_token, current_session.salt, to_number(
    tasker.app_settings.get( 'TOKEN_HASH_ITER', 256 ) ), to_number(
    tasker.app_settings.get( 'TOKEN_PART_LENGTH', 128 ) ) );
    -- next, hash the token with the "pepper" from the token
    candidate_token_hash := gen_hash( candidate_token_hash, candidate_salt, to_number(
    tasker.app_settings.get( 'TOKEN_HASH_ITER', 256 ) ), to_number(
    tasker.app_settings.get( 'TOKEN_PART_LENGTH', 128 ) ) );
    -- check if we have a match
    IF current_session.token = candidate_salt || candidate_token_hash THEN
      -- set the user that matches
      p_auth_user := current_session.user_id;
      -- generate the next token
      make_unique_token( current_session.salt, client_token, db_token, dev_token );
      p_next_token := client_token;
       UPDATE sessions
      SET token = db_token, expiry = sysdate + to_number( tasker.app_settings.get(
        'TOKEN_EXPIRY' ) ), client_token = dev_token
        WHERE id = p_session_id;
      COMMIT;
      -- and indicate verification
      RETURN 'Y';
    END IF;
  END IF;
  RETURN 'N';
END;
END SECURITY;

