  IF task.owner         != user_person_id THEN
    IF task.assigned_to != user_person_id THEN
      IF NOT tasker.security.can_user ( p_as_user, CAN_MODIFY_ANY_TASK() ) THEN
        tasker.utils.E_ACCESS_DENIED();
