FUNCTION create_user(
    p_user_id  VARCHAR2,
    p_password VARCHAR2,
    p_as_user  VARCHAR2 )
  RETURN VARCHAR2
AS
pragma autonomous_transaction;
  new_salt VARCHAR2(4000);
  new_hash VARCHAR2(4000);
